//路由分发处理
exports.router = function(req, res){
	var pathname = lib.url.parse(req.url).pathname;
	console.log(req.url);
    if(pathname == 'favicon.ico'){
    	return;
    }else if(pathname == '/' || pathname == '/index'){
		var index = require(LIB + '/index');
		index.index(req, res);
		return;
	}else{
		var module = pathname.substr(1);
		console.log(module);
		switch (module){
			case 'differentBlogs' :
			    var differentBlogs = require(LIB + '/differentBlogs');
			    differentBlogs.differentBlogs(req, res);
			    break;

			case 'blogExperts' :
			    var blogExperts = require(LIB + '/blogExperts');
			    blogExperts.blogExperts(req, res);
			    break;

			case 'blogsShow' :
			    var blogsShow = require(LIB + '/blogsShow');
			    blogsShow.blogsShow(req, res);
			    break;

			case 'mySpace' :
			    var mySpace = require(LIB + '/mySpace');
			    mySpace.mySpace(req, res);
			    break;

			case 'myFocus' :
			    var myFocus = require(LIB + '/myFocus');
			    myFocus.myFocus(req, res);
			    break;

			case 'myCollect' :
			    var myCollect = require(LIB + '/myCollect');
			    myCollect.myCollect(req, res);
			    break;

			case 'login' :
			    var login = require(LIB + '/login');
			    login.login(req, res);
			    break;

			case 'register' :
			    var register = require(LIB + '/register');
			    register.register(req, res);
			    break;

			case 'loginCheck' :
			    var loginCheck = require(LIB + '/loginCheck');
			    loginCheck.loginCheck(req, res);
			    break;

			case 'registerDeal' :
			    var registerDeal = require(LIB + '/registerDeal');
			    registerDeal.registerDeal(req, res);
			    break;

			case 'addFocus' :
			    var addFocus = require(LIB + '/addFocus');
			    addFocus.addFocus(req, res);
			    break;

			case 'addCollect' :
			    var addCollect = require(LIB + '/addCollect');
			    addCollect.addCollect(req, res);
			    break;

			case 'userMessage' :
			    var userMessage = require(LIB + '/userMessage');
			    userMessage.userMessage(req, res);
			    break;

			case 'messageChange' :
			    var messageChange = require(LIB + '/messageChange');
			    messageChange.messageChange(req, res);
			    break;

			case 'photoUpload' :
			    var photoUpload = require(LIB + '/photoUpload');
			    photoUpload.photoUpload(req, res);
			    break;

			case 'writeBlog' :
			    var writeBlog = require(LIB + '/writeBlog');
			    writeBlog.writeBlog(req, res);
			    break;

			case 'addBlog' :
			    var addBlog = require(LIB + '/addBlog');
			    addBlog.addBlog(req, res);
			    break;

			default :
			    lib.staticModule.dealWithStatic(req, res);
		}
	}
};